fest:template:

    var time = new Date();

    time = {
        minutes : String(time.getMinutes()),
        seconds : String(time.getSeconds()),
        hours : String(time.getHours())
    };

    time = time.hours + ":" + time.minutes + ":" + time.seconds;


<p class="chat-message msg.access">
    msg.author  time  :
    msg.message
